import React from 'react';
import './Modal.css';
class Modal extends React.Component {
  render() {
    if(!this.props.show) {
      return null;
    }

    return (
      <div className="popup-backdrop">
        <div className="popup">
          <button className="popup-close" onClick={this.props.onClose}>Am adaugat cartea la favorite</button>
          {this.props.children}
        </div>
      </div>
    );
  }
}

export default Modal;
