import React, { Component } from 'react';
import './Book.css';
import Button from 'react-bootstrap/lib/Button';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import {getJson, post} from "../data/DataUtils";
import Modal from './Modal';


const display = {
  display: 'block',
  width: '800px',
  height: '800px',
  left: '19%',
  top: '30%'


};
const hide = {
  display: 'none'
};

class Book extends Component {
 constructor(props) {
    super(props);

    this.state = {
        idBook: this.props.match.params.idBook,
        detailsBook: [],
        similarBooks: [],
        toggle: false,
        addFavoriteBook:'ok'
    };
        this.toggle = this.toggle.bind(this);
        this.buttonFormatter = this.buttonFormatter.bind(this);
        this.handleAddFavoriteBook = this.handleAddFavoriteBook.bind(this);
  }
   toggle() {

 console.log("idBOOOK:::: " + this.state.idBook);
     this.setState(prevState => ({
       toggle: !prevState.toggle
     }));

   }


 componentDidMount() {

   var obj = {
          "idBook": this.state.idBook
         };
   console.log("OBJ::: ", obj);

   post("bookById", obj).then(response => {
         this.setState({
                      detailsBook: response,
         })
   });
//   post("similarBooks", obj).then(response => {
//        this.setState({
//                     similarBooks: response,
//        })
//   });

  }

 handleClick(id) {
        post("bookById", {'idBook': id}).then(response => {
            this.setState({detailsBook: response,
            show: false
            })
        });
//        post("similarBooks", {'idBook': id}).then(response => {
//            this.setState({similarBooks: response})
//        });
 }

 handleAddFavoriteBook(idBook, idUser) {
    console.log("idUser:: ", idUser);
    console.log("idBook:: ", idBook);
    // TODO adaugi cartea la favorite
    post("favorite", {"idUser": idUser,
                      "idBook": idBook}).then(response => {
                      console.log("RASPUNS FAVORITE BOOK::: " + response);
                this.setState({addFavoriteBook: response
                              })
            });
    // TODO in funtie de raspuns afisezi raspunsul in modala

 }


  buttonFormatter(cell, row){
        console.log("row= ", row.id);
        var path = "/book/" + row.id;
      return  <Button onClick={() => this.handleClick(row.id)}>Details Book
              </Button>
  }

  render() {


    console.log("!prevState.toggle------------------------------ " + this.state.toggle);
  var modal = [];

  console.log("SA VEDEM DACA ESTE OK" + this.state.addFavoriteBook=='ok')

    modal.push(
      <div className="modal"   style={this.state.toggle ? display : hide}>
      <div className="modal-content">
        <h4>Add favorite book</h4>
        <p>You have successfully added the book to your favorites list</p>
        <div className="modal-footer">
            <a className="btn" onClick={this.toggle}>Agree</a>
        </div>
      </div>
    </div>
    );
 // }

    if (this.addFavoriteBook=='cannot set favorite book') {
      modal.push(
        <div className="modal"   style={this.state.toggle ? display : hide}>
        <div className="modal-content">
          <h4>Add favorite book</h4>
          <p>Cannot set favorite book</p>
          <div className="modal-footer">
              <a className="btn" onClick={this.toggle}>Agree</a>
          </div>
        </div>
      </div>
      );
    }

    return (
      <div>
       {modal}
        <h1>{this.state.detailsBook.title}</h1>
        <div id="container">
            <div  id="first">
                <span style={{ "text-align": "justify"}}>
                    <div>
                         <button type="button" class="btn btn-light"  onClick={this.toggle}>Add favorite</button>
                    </div>
                       &nbsp;
                    <div>
                        <b> Author: </b> {this.state.detailsBook.author}
                    </div>
                       &nbsp;
                    <div>
                        <b> Description: </b> {this.state.detailsBook.description}
                    </div>
                        &nbsp;
                    <div>
                        <b> Price: </b> {this.state.detailsBook.price} Ron
                    </div>
                       &nbsp;
                    <div>
                        <b> Pages: </b> {this.state.detailsBook.pages}
                    </div>
                       &nbsp;
                    <div>
                        <b> Publisher: </b> {this.state.detailsBook.publisher}
                    </div>
                       &nbsp;
                    <div>
                        <b> Language: </b> {this.state.detailsBook.language}
                    </div>
                       &nbsp;

                    <div>
                    <b> Customer Reviews: </b> {this.state.detailsBook.customer_reviews}
                    </div>
                       &nbsp;
                    <div>
                    <b> Stars: </b> {this.state.detailsBook.stars}
                    </div>
                </span>
            </div>
            <div  id="second" >
                            <BootstrapTable data={this.state.similarBooks} striped={true} hover={true}  search pagination>
                                  <TableHeaderColumn width="123" dataField="title" isKey={true} dataAlign="center" dataSort={true}>Title</TableHeaderColumn>
                                  <TableHeaderColumn width="123" dataField="author" dataSort={true}>Author</TableHeaderColumn>
                                  <TableHeaderColumn width="123" dataField="button" dataFormat={this.buttonFormatter}>Details</TableHeaderColumn>
                            </BootstrapTable>
            </div>
        </div>
      </div>
    );
  }
}

export default Book;
